package While;

public class PrintEvenNumbresBetween0to100 {

	public static void main(String[] args) {
		
		
		// TODO Auto-generated method stubwhile
		
		int i=0;
		while(i<=100)
		{
			if(i%2==0)
			{
				
				System.out.println(i+" even");
			}
			else
			{
				
				System.out.println(i+" odd");
				
			}
			
			i++;
		}
		
//print 100 1 odd numbers  using while
		//print 100 0 even odd numbers using while
		
		//print  1st table  1*1=1    1*2=2         1*10=10  using while
		
		//print  1st table  1*10=10    1*9=9         1*1=1 using while
		
		
	}

}
