package dowhile;

public class Printnumbersfrom5000to4333indrementorder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=5000;//declaration
		do
		{
			
			System.out.println(i);// statement you want execute
			i--;// increment or decrement
			
		}while(i>=4333); 
	}

}
